import { CHANGE_STATE, LOADING_PAGE_STATE } from "../constant/action-type";

export function buttonClickAction(payload) {
  return function(dispatch) {
    dispatch({
      type: LOADING_PAGE_STATE,
      value: payload.value
    }).then(() => {
      setTimeout(function() {
        dispatch(
          {
            type: LOADING_PAGE_STATE,
            value: false
          },
          2000
        );
      });
    });
  };
}
