import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import CreditCard from './creditcard';
import CardDetails from './cardDetails';
import CreditCardBack from './creditcard-Back';
import { connect } from "react-redux";

const styles = {
  card: {
    maxWidth:500,
    margin:'25px auto'
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
};
const button_style = {
    margin: '0 auto'
}

class SimpleCard extends Component{



  classes= this.props.classes;

// shouldComponentUpdate(nextstate,nextprops){
//   console.log("this should");
//       if(this.props.frontSide !== nextprops.frontSide){

//         return true;
//       }
//       else{
//         return false;
//       }

//   }

render(){
  return (
    <Card className={this.classes.card}>
      <CardContent>
          {this.props.frontSide? <CreditCard cardNumber={this.props.cardNumber} preview = {this.props.preview}/>:<CreditCardBack/>}
      </CardContent>
      <CardContent>
        <CardDetails/>
      </CardContent>
      <CardActions>
        <Button variant="contained" color="primary" className={this.classes.button} disabled={this.props.buttonState} style={button_style}>
            Submit      
        </Button>
      </CardActions>
    </Card>
  );
}
}
SimpleCard.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SimpleCard);
