import{
    CHANGE_STATE,
    SUBMIT_DETAILS,
} from '../constant/action-type';

export function buttonClickAction(payload){

    return function(dispatch){
        if(payload.name === 'reset'){
            dispatch({
                    type:CHANGE_STATE,
                    value: payload.value

            })
        }

        if(payload.name === 'submit'){
            dispatch({
                    type:SUBMIT_DETAILS,
                    value: payload.value

            })
        }
    }
}
