import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import { connect } from "react-redux";
import {swapCard} from '../action/textfield-onclick-action'

const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
  },
  dense: {
    marginTop: 16,
  },
  menu: {
    width: 200,
  },

});
 const table_style = {
    margin:'20px auto '
 }

class FilledTextFields extends React.Component {
  state = {
    name: 'Cat in the Hat',
    age: '',
    multiline: 'Controlled',
    currency: 'EUR',
  };

  handleChange = name => event => {
    this.setState({
      [name]: event.target.value,
    });
  };

  // onClickOnTextField = (event)=>{
  //   var id = event.target.id;
  //   console.log(id)

  // }

  render() {
    const { classes } = this.props;

    return (
      <form className={classes.container} noValidate autoComplete="off">
      <table style={table_style}>
      <tr>
          <td>
            <TextField
            id="filled-dense"
            label="Card Number"
            className={classNames(classes.textField, classes.dense)}
            margin="dense"
            variant="filled"
            onClick={this.setCreditCardSide}
            style={{backgroundColor:'#fafafa'}}
            />
          </td>
          <td>
            <TextField
            id="filled-dense"
            label="Name"
            className={classNames(classes.textField, classes.dense)}
            margin="dense"
            variant="filled"
            onClick={this.setCreditCardSide}
            />
          </td>
      </tr> 
      <tr>
          <td>
            <TextField
            id="filled-dense"
            label="Security Code"
            className={classNames(classes.textField, classes.dense)}
            margin="dense"
            variant="filled"
            onClick={this.setCreditCardSide}
            />
          </td>
          <td>
            <TextField
            id="dense"
            label="EXPIRE Date"
            className={classNames(classes.textField, classes.dense)}
            margin="dense"
            variant="filled"
            onClick={this.setCreditCardSide}
            />
          </td>
      </tr> 
      </table>

      </form>
    );
  }
}
const mapDispatchToProps = (dispatch, props) =>({
    setCreditCardSide(event){
      var id = event.target.id;
      return()=>{
        dispatch(swapCard(id))
      }
    }
})  
const mapActionsToProps = (dispatch, props) => {
  return bindActionCreators(
    {

    }
  )
}


FilledTextFields.propTypes = {
  classes: PropTypes.object.isRequired,
};

const CardDef = withStyles(styles)(FilledTextFields);


export default connect(mapDispatchToProps)(CardDef);
