import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import { connect } from "react-redux";
import { swapCard } from "../action/textfield-onclick-action";
import { updateCardValues } from "../action/update-cardvalue-action";
import { bindActionCreators } from "redux";
import { creditCardValidator } from "../service/validator";
import InputAdornment from "@material-ui/core/InputAdornment";
const styles = theme => ({
  container: {
    display: "flex",
    flexWrap: "wrap"
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit
  },
  dense: {
    marginTop: 16
  },
  menu: {
    width: 200
  }
});

class FilledTextFields extends React.Component {
  state = {
    specialText_cardNumber: "",
    specialText_cardName: "",
    specialText_cardDate: "",
    specialText_cardCVC: "",
    error_state_cardNumber: false,
    error_state_cardName: false,
    error_state_cardDate: false,
    error_state_cardCVC: false,
    cardImage: "https://img.icons8.com/ios/48/000000/bank-card-back-side.png",
    cardNumber: 0,
    error: false
  };

  handleChange = event => {
    var name = event.target.name;
    const re = /^[0-9\b]+$/;
    if (
      (event.target.value === "" || re.test(event.target.value)) &&
      name === "cardNumber"
    ) {
      let value = event.target.value;
      console.log(value);
      var payload = {
        name: name,
        value: value
      };
      this.props.updateCardValues(payload);
    }
    if (name === "cardName") {
      payload = {
        name: name,
        value: event.target.value
      };
      this.props.updateCardValues(payload);
    }
    if (name === "expireDate") {
      payload = {
        name: name,
        value: event.target.value
      };
      this.props.updateCardValues(payload);
    }
    if (name === "cardSecurityCode") {
      payload = {
        name: name,
        value: event.target.value
      };
      this.props.updateCardValues(payload);
    }
  };

  onKeyDown = event => {
    var name = event.target.name;
    if (event.keyCode === 8) {
      var payload = {
        name: name,
        value: 0
      };
      this.props.updateCardValues(payload);
    }
  };

  onClickOnTextField = event => {
    var str = this.props.cardNumber2.toString();
    if (str.length < 16) {
      console.log("onclick");
      this.setState({ cardImage: "", specialText: "" });
    }
    var _id = event.target.id;
    this.setState({
      id: _id
    });
    this.props.swapCard({ id: _id });
  };

  callValidator = event => {
    var name = event.target.name;
    var expireDate_str = this.props.expireDate;
    var date = "";

    if (expireDate_str.length === 4) {
      for (let i = 0; i < expireDate_str.length; i++) {
        date += expireDate_str.charAt(i);
        if (i === 1) {
          date += "/";
        }
      }
    }
    var creditCard = {
      value: this.props.cardNumber2,
      name: name
    };

    if (name === "cardNumber") {
      if (!creditCardValidator(creditCard) || creditCard.value === 0) {
        this.setState({
          specialText_cardNumber: "invalid number",
          error_state_cardNumber: true,
          error: true,
          cardImage:
            "https://img.icons8.com/ios/48/000000/bank-card-back-side.png"
        });
      } else {
        this.setState({
          specialText_cardNumber: "",
          error_state_cardNumber: false
        });
        var str = this.props.cardNumber2.toString();
        if (str.charAt(0) === "4") {
          this.setState({
            cardImage: "https://img.icons8.com/color/48/000000/visa.png"
          });
        }
        if (str.charAt(0) === "5") {
          this.setState({
            cardImage: "https://img.icons8.com/color/48/000000/mastercard.png"
          });
        }
      }
    }
    if (name === "cardName") {
      console.log("card name", event.target.value.length);
      if (event.target.value.length === 0) {
        this.setState({
          specialText_cardName: "Name is required",
          error_state_cardName: true,
          error: true
        });
      } else {
        this.setState({
          specialText_cardName: "",
          error_state_cardName: false,
          error: false
        });
      }
    }
    if (name === "expireDate") {
      creditCard = {
        value: date,
        name: name
      };
      if (!creditCardValidator(creditCard) || date.length < 5) {
        this.setState({
          specialText_cardDate: "invalid date",
          error_state_cardDate: true,
          error: true
        });
      } else {
        this.setState({
          specialText_cardDate: "",
          error_state_cardDate: false,
          error: false
        });
      }
    }
    if (name === "cardSecurityCode") {
      creditCard = {
        value: event.target.value,
        name: name
      };
      if (!creditCardValidator(creditCard) || creditCard.value < 3) {
        this.setState({
          specialText_cardCVC: "invalid CVC",
          error_state_cardCVC: true,
          error: true
        });
      } else {
        this.setState({
          specialText_cardCVC: "",
          error_state_cardCVC: false,
          error: false
        });
      }
    }
  };

  render() {
    const { classes } = this.props;
    var cardNum = this.props.cardNumber2.toString();
    var length_cardNum = cardNum.length;

    var dateNum = this.props.expireDate.toString();
    var length_date = dateNum.length;

    var cvc = this.props.securityCode.toString();
    var length_cvc = cvc.length;

    return (
      <form className={classes.container} noValidate autoComplete="off">
        <TextField
          id="cardNumber"
          label="Card Number"
          name="cardNumber"
          type="integer"
          style={{ margin: 8 }}
          fullWidth
          margin="normal"
          variant="outlined"
          InputLabelProps={{
            shrink: true
          }}
          onClick={this.onClickOnTextField}
          onChange={this.handleChange}
          onBlur={this.callValidator}
          helperText={this.state.specialText_cardNumber}
          error={this.state.error_state_cardNumber}
          onInput={e => {
            e.target.value = Math.max(0, parseInt(e.target.value))
              .toString()
              .slice(0, 16);
          }}
          InputProps={{
            startAdornment: (
              <InputAdornment position="end">
                <img src={this.state.cardImage} alt="Card-Type" />
              </InputAdornment>
            )
          }}
        />
        <TextField
          id="filled-dense"
          label="Name"
          name="cardName"
          style={{ margin: 8 }}
          fullWidth
          margin="normal"
          variant="outlined"
          InputLabelProps={{
            shrink: true
          }}
          onClick={this.onClickOnTextField}
          onBlur={this.callValidator}
          helperText={this.state.specialText_cardName}
          error={this.state.error_state_cardName}
          onChange={this.handleChange}
        />

        <table>
          <tr>
            <td>
              <TextField
                id="filled-dense"
                label="Expire Date"
                name="expireDate"
                className={classes.textField}
                margin="normal"
                variant="outlined"
                onClick={this.onClickOnTextField}
                onBlur={this.callValidator}
                helperText={this.state.specialText_cardDate}
                error={this.state.error_state_cardDate}
                onChange={this.handleChange}
              />
            </td>
            <td>
              <TextField
                id="dense"
                label="CVC"
                name="cardSecurityCode"
                type="string"
                className={classes.textField}
                margin="normal"
                variant="outlined"
                onClick={this.onClickOnTextField}
                onBlur={this.callValidator}
                helperText={this.state.specialText_cardCVC}
                error={this.state.error_state_cardCVC}
                onChange={this.handleChange}
                onInput={e => {
                  e.target.value = Math.max(0, parseInt(e.target.value))
                    .toString()
                    .slice(0, 4);
                }}
              />
            </td>
          </tr>
        </table>
      </form>
    );
  }
}
const mapActionsToProps = (dispatch, props) => {
  console.log("dispatch calling");
  return bindActionCreators(
    {
      swapCard: swapCard,
      updateCardValues: updateCardValues
    },
    dispatch
  );
};

FilledTextFields.propTypes = {
  classes: PropTypes.object.isRequired
};

const CardDef = withStyles(styles)(FilledTextFields);

export default connect(
  null,
  mapActionsToProps
)(CardDef);
