import {
  SWAP_CARD_FRONT,
  SWAP_CARD_BACK
} from '../constant/action-type';



  function changeCardShowbleSide(state ={} , action) {
    console.log("index is calling")
    switch(action.type){
      case SWAP_CARD_BACK:
        let newState1 = Object.assign({},state);
        //console.log("newStaet1",newState1);
        newState1.creditCard.frontSide = false;
        newState1.creditCard.backSide = true;
        return newState1;
      case SWAP_CARD_FRONT:
        newState1 = Object.assign({},state);
        newState1.creditCard.frontSide = true;
        newState1.creditCard.backSide = false;
        return newState1;
      default:
          return state;
    }

  };
  export default changeCardShowbleSide;
