import { LOADING_PAGE_STATE } from "../constant/action-type";

export function buttonClickAction(payload) {
  return async function(dispatch) {
    dispatch =>
      ({
        type: LOADING_PAGE_STATE,
        value: payload.value
      }.then(() => {
        setTimeout(function(dispatch) {
          dispatch({
            type: LOADING_PAGE_STATE,
            value: false
          });
        }, 5000);
      }));
  };
}
