import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import { connect } from "react-redux";
import {swapCard} from '../action/textfield-onclick-action'
import {updateCardValues} from  '../action/update-cardvalue-action';
const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
  },
  dense: {
    marginTop: 16,
  },
  menu: {
    width: 200,
  },

});
 const table_style = {
    margin:'20px auto '
 }

class FilledTextFields extends React.Component {


  state = {
    cardNumber:[],
    age: '',
    multiline: 'Controlled',
    currency: 'EUR',
    id:''
  };

  handleChange = (event)=> {
      var name = event.target.name;
      var value = event.target.value;
  };

  onClickOnTextField =(event)=>{
    var _id = event.target.id;
    this.setState({
      id:_id
    })
    this.props.swapCard({id:_id});
  }

  render() {
    const { classes } = this.props;

    return (
      <form className={classes.container} noValidate autoComplete="off">
      <table style={table_style}>
      <tr>
          <td>
            <TextField
            id="filled-dense"
            label="Card Number"
            name = "cardNumber"
            className={classNames(classes.textField, classes.dense)}
            margin="dense"
            variant="filled"
            onClick={this.onClickOnTextField}
            style={{backgroundColor:'#fafafa'}}
            onChange ={this.handleChange}
            />
          </td>
          <td>
            <TextField
            id="filled-dense"
            label="Name"
            name="CardName"
            className={classNames(classes.textField, classes.dense)}
            margin="dense"
            variant="filled"
            onClick={this.onClickOnTextField}
            />
          </td>
      </tr> 
      <tr>
          <td>
            <TextField
            id="filled-dense"
            label="Security Code"
            name="cardSecurityCode"
            className={classNames(classes.textField, classes.dense)}
            margin="dense"
            variant="filled"
            onClick={this.onClickOnTextField}
            />
          </td>
          <td>
            <TextField
            id="dense"
            label="EXPIRE Date"
            name="cardExpireDate"
            className={classNames(classes.textField, classes.dense)}
            margin="dense"
            variant="filled"
            onClick={this.onClickOnTextField}
            />
          </td>
      </tr> 
      </table>

      </form>
    );
  }
}
const mapDispatchToProps = (dispatch, props) =>{
  console.log("dispatch calling")
  return{
    swapCard: (id)=>dispatch(swapCard(id))

  }

}


FilledTextFields.propTypes = {
  classes: PropTypes.object.isRequired,
};

const CardDef = withStyles(styles)(FilledTextFields);


export default connect(null,mapDispatchToProps)(CardDef);
