import {
UPDATE_CARD_NUMBER ,
UPDATE_CARD_NAME, 
UPDATE_CARD_DATE,
UPDATE_CARD_SECURITY_CODE 
} from '../constant/action-type';
import thunk from 'redux-thunk';
import {  createStore,
    applyMiddleware,
    compose } from 'redux';
import rootReducer from '../reducer/rootReducer'
 export function updateCardValues(payload){

    return dispatch => {

        if(payload.name === 'cardNumber'){
               return( {
                    type: UPDATE_CARD_NUMBER,
                    value:payload.value
                }
               )

        }
        if(payload.name === 'cardName'){
            return(
                {
                    type: UPDATE_CARD_NAME,
                    value:payload.value
                }
            )
        }
        if(payload.name === 'cardSecurityCode'){
            return(
                {
                    type: UPDATE_CARD_SECURITY_CODE,
                    value:payload.value
                }
            )
        }
        if(payload.name === 'cardExpireDate'){
            return(
                {
                    type: UPDATE_CARD_DATE,
                    value:payload.value
                }
            )
        }
    }

}
export default compose(applyMiddleware(thunk))(createStore)(rootReducer);
