import {
  SWAP_CARD_FRONT,
  SWAP_CARD_BACK
} from '../constant/action-type';


  function rootReducer(state = {}, action) {
    switch(action.type){
      case SWAP_CARD_BACK:
        let newState = Object.assign({},state,{frontSide:false,backSide:true});
        console.log("new State",newState);
        return newState;
      case SWAP_CARD_FRONT:
        return Object.assign({},state,{frontSide:true,backSide:false});
      default:
          return state;
    }

  };
  export default rootReducer;
