import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import CreditCard from './creditcard';
import CardDetails from './cardDetails';
import CreditCardBack from './creditcard-Back';
import { connect } from "react-redux";

const styles = {
  card: {
    maxWidth:500,
    margin:'25px auto'
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
};
const button_style = {
    margin: '0 auto'
}

function SimpleCard(props) {
  const { classes } = props;
   console.log(props);

   const CardShowbleSide = (props)=>{
     console.log('Cardshow is calling');
     if(props.frontSide){
       return <CreditCard/>
     }
     else{
       return <CreditCardBack/>
     }
   }

  return (
    <Card className={classes.card}>
      <CardContent>
          <CardShowbleSide/>
          <CardDetails/>
      </CardContent>
      <CardActions>
        <Button variant="contained" color="primary" className={classes.button} disabled={props.buttonState} style={button_style}>
            Submit      
        </Button>
      </CardActions>
    </Card>
  );
}

SimpleCard.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SimpleCard);
