import { LOADING_PAGE_STATE } from "../constant/action-type";

export function buttonClickAction(payload) {
  return function(dispatch) {
    dispatch({
      type: LOADING_PAGE_STATE,
      value: payload.value
    });
    return new Promise(function(resolve, reject) {
      setTimeout(dispatch =>
        dispatch({
          type: LOADING_PAGE_STATE,
          value: false
        })
      );
    });
  };
}
