import {
  CHANGE_STATE,
  SUBMIT_DETAILS,
  LOADING_PAGE_STATE
} from "../constant/action-type";

export function buttonClickAction(payload) {
  return function(dispatch) {
    if (payload.name === "reset") {
      dispatch({
        type: CHANGE_STATE,
        value: payload.value
      });
    }

    if (payload.name === "submit") {
      dispatch({
        type: LOADING_PAGE_STATE,
        value: payload.value
      });
    }
  };
}
