import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import MAX_LENGTH from '../constant/action-type';

    const styles = {
        card: {
          maxWidth:350,
          margin:'20px auto ',
          backgroundColor: '#eceff1'
        },
        media: {
            height: 50,
          },
        bullet: {
          display: 'inline-block',
          margin: '0 2px',
          transform: 'scale(0.8)',
        },
        title: {
          fontSize: 14,
        },
        pos: {
          marginBottom: 12,
        },
      };

      const Style_chip = {
            maxWidth: 50,
            marginLeft:5,
            position:'relative',
            right: 100

      }


      const Style_visa = {
        maxWidth: 50,
        marginLeft:5,
        position:'relative',
        left: 100
      }

    const style_bullet = {
        padding: 5,
        margin: 5,
        display: 'inline-block'
    }
    const style_bullet2 = {
        padding: 2,
        margin: 2,
        position:'relative',
        display: 'inline-block',
        left:120
    }


 class SimpleCard extends Component{


    Bullet1 = ()=>{

      return(
          <div style={style_bullet}>
              <span className='bullet'>• </span>
              <span className='bullet'>• </span>
              <span className='classes'>• </span>
              <span className='classes'>• </span>
          </div>
      )
   }

   Bullet2 = ()=>{

      return(
          <div style={style_bullet2}>
              <span className='bullet'>• </span>
              <span className='bullet'>• </span>
          </div>
      )
   }



    number = (MAX_LENGTH)=>{
      var length_arr = this.props.cardNumber.length;
      while(MAX_LENGTH>length_arr){
        if( length_arr %5 === 0){
          return(
            <span className='bullet'>{' '}</span>
          );
        }
          else{
            if(this.props.preview){
              return(
                <span className='bullet'>• </span>
              )
            }
            else{
              return(
                <span className='bullet'>• </span>
              )
            } 
          }
        }
      }


      render(){
        const {classes} = this.props;
        const {Bullet1, Bullet2} = this;
        const Number = this.number;

        return (
          <Card className={classes.card}>
            <CardContent>
              <img style = {Style_chip} src="http://jamesdelaney.ie/dev/chip.png" alt="chip"/>
              <img style = {Style_visa} src="https://webiconspng.com/wp-content/uploads/2017/09/Visa-PNG-Image-76712.png" alt="visa" />
              <Typography variant="h5" component="h2">
                <tr>
                  <Number/>
                </tr>
              </Typography>
              <Typography variant="Title" component="h6" align="right">
                valid thru
              </Typography>
              <tr>
                <td>
                    <Typography variant="subheading" component="h6" align="left">
                    YOUR NAME HERE
                    </Typography>
                </td>
                <td>
                    <Typography variant="Title" component="h6" align="right" >
                        <Bullet1/>
                        <Bullet2 /> 
                    </Typography>
                </td>
              </tr>
            </CardContent> 
          </Card>
        );
      }
    }
      SimpleCard.propTypes = {
        classes: PropTypes.object.isRequired,
      };

     export default withStyles(styles)(SimpleCard);
