import lunh from 'luhn';



const creditCardValidator = (creditCard)=>{
    var luhn = lunh;
    if(creditCard.name === 'cardNumber'){
        return luhn.validate(creditCard.number);
    }


}

module.exports = {
    creditCardValidator
 }
