import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import { connect } from "react-redux";
import {swapCard} from '../action/textfield-onclick-action'
import {updateCardValues} from  '../action/update-cardvalue-action';
import { bindActionCreators } from "redux";
import {creditCardValidator} from '../service/validator';

const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
  },
  dense: {
    marginTop: 16,
  },
  menu: {
    width: 200,
  },
});

 const table_style = {
    margin:'20px auto '
 }

class FilledTextFields extends React.Component {

  state = {
    specialText:"" ,
    error_state:false,
    cardImage :"",
    cardNumber:0
  }


  handleChange = (event)=> {
      var name = event.target.name;
      const re = /^[0-9\b]+$/;
      if((event.target.value === '' ||re.test(event.target.value)) && name === 'cardNumber' ){
        let value = event.target.value;
        console.log(value);
        var payload = {
          name:name,
          value:value
        }
        this.props.updateCardValues(payload);
      }
      if( name === 'cardName'){
        payload = {
          name:name,
          value:event.target.value
        }
        this.props.updateCardValues(payload);
      }
      if( name === 'expireDate'){
        payload = {
          name:name,
          value:event.target.value
        }
        this.props.updateCardValues(payload);
      } 
      if( name === 'cardSecurityCode'){
        payload = {
          name:name,
          value:event.target.value
        }
        this.props.updateCardValues(payload);
      }  
  }

  onKeyDown = (event)=>{
    var name = event.target.name;
    if (event.keyCode === 8) {
      var payload = {
        name:name,
        value:0
      }
      this.props.updateCardValues(payload);
    }


  }


  onClickOnTextField =(event)=>{
    var _id = event.target.id;
    this.setState({
      id:_id
    })
    this.props.swapCard({id:_id});
  }

  callValidator(value){  
    var creditCard = {
      number:value,
      name:'cardNumber'
    }
    var str = value.toString();
    if(str.length === 16){
      console.log("number of time")
      this.setState({cardNumber:value});
      if(str.charAt(0) === '4'){

        this.setState({cardImage:"https://img.icons8.com/color/48/000000/visa.png"})

      }
      if(str.charAt(0) === '5'){
        this.setState({cardImage:"https://img.icons8.com/color/48/000000/mastercard.png"})

      }
      if(!creditCardValidator(creditCard)){
        this.setState({specialText:"invalid number",error_state:true});
      }
    }
    }


  render() {
    const { classes } = this.props;
    var cardNum = this.props.cardNumber2.toString();
    var length_cardNum = cardNum.length;

    var dateNum = this.props.expireDate.toString();
    var length_date = dateNum.length;

    var cvc = this.props.securityCode.toString();
    var length_cvc = cvc.length;

    return (
      <form className={classes.container} noValidate autoComplete="off">

        <TextField
        id="cardNumber"
        label="Card Number"
        name = "cardNumber"
        type = "number"
        style={{ margin: 8 }}
        placeholder="Placeholder"
        fullWidth
        margin="normal"
        variant="outlined"
        InputLabelProps={{
          shrink: true,
        }}
        onClick={this.onClickOnTextField}
        onChange ={this.handleChange}

        onBlur ={this.props.cardNumber2.length===16?this.callValidator(this.props.cardNumber2):''}
        helperText={this.state.specialText}
        error = {this.state.error_state}

        />
        <img src = {this.state.cardImage} alt="Card-Type"/>

        <TextField
        id="filled-dense"
        label="Name"
        name="cardName"
        style={{ margin: 8 }}
        placeholder="Placeholder"
        helperText="Full width!"
        fullWidth
        margin="normal"
        variant="outlined"
        InputLabelProps={{
          shrink: true,
        }}
        onClick={this.onClickOnTextField}
        onChange ={this.handleChange} 
        />

      <table>
        <tr>
          <td>
      <TextField
      id="filled-dense"
      label="Expire Date"
      name="expireDate"
      type="number"
      className={classes.textField}
      helperText="Some important text"
      margin="normal"
      variant="outlined"
      onClick={this.onClickOnTextField}
      onChange ={this.handleChange}
      disabled = {length_date<4? false:true}
      />
        </td>
        <td>
      <TextField
      id="dense"
      label="CVC"
      name="cardSecurityCode"
      className={classes.textField}
      helperText="Some important text"
      margin="normal"
      variant="outlined"
      onClick={this.onClickOnTextField}
      onChange ={this.handleChange}
      disabled = {length_cvc<3? false:true}
      />
      </td>
      </tr>
      </table>
      </form>
    );
  }
}
const mapActionsToProps = (dispatch, props) =>{
  console.log("dispatch calling")
  return bindActionCreators ({
    swapCard: swapCard,
    updateCardValues: updateCardValues

  },
  dispatch
  );

}


FilledTextFields.propTypes = {
  classes: PropTypes.object.isRequired,
};

const CardDef = withStyles(styles)(FilledTextFields);


export default connect(null,mapActionsToProps)(CardDef);
