import{
    CHANGE_STATE,
    SUBMIT_DETAILS,
} from '../constant/action-type';



function buttonClickEvent(state={},action){
    switch(action.type){
        case CHANGE_STATE:
            return state = null ;
        case SUBMIT_DETAILS:
            let newState = Object.assign({},state);
            return newState;
        default:
            return state;
    }
}

export default buttonClickEvent;
