import lunh from "luhn";

export const creditCardValidator = creditCard => {
  var luhn = lunh;
  if (creditCard.name === "cardNumber") {
    var is_valid = luhn.validate(creditCard.value.toString());
    console.log("validator is calling", is_valid);
    return is_valid;
  }
  if (creditCard.name === "expireDate") {
    checkExp(creditCard.value);

    function normalizeYear(year) {
      // Century fix
      var YEARS_AHEAD = 20;
      if (year < 100) {
        var nowYear = new Date().getFullYear();
        year += Math.floor(nowYear / 100) * 100;
        if (year > nowYear + YEARS_AHEAD) {
          year -= 100;
        } else if (year <= nowYear - 100 + YEARS_AHEAD) {
          year += 100;
        }
      }
      return year;
    }

    function checkExp(date) {
      var re = /^\s*(0?[1-9]|1[0-2])\/(\d\d|\d{4})\s*$/;
      var match = re.test(date);
      if (!match) {
        alert(
          "Input string isn't match the expiration date format or date fragments are invalid."
        );
        return;
      }
      var exp = new Date(
        normalizeYear(1 * match[2]),
        1 * match[1] - 1,
        1
      ).valueOf();
      var now = new Date();
      var currMonth = new Date(now.getFullYear(), now.getMonth(), 1).valueOf();
      if (exp <= currMonth) {
        alert("Expired");
        return false;
      } else {
        return true;
      }
    }
  }
};
